mapper.select:

SELECT 
	${fields}
FROM 
	inscription 
WHERE 
	id = #{id, jdbcType=BIGINT}


SELECT 
	${fields}
FROM
	inscription
WHERE 
	score>= #{min_score, jdbcType=INTEGER} 
	AND
	score< #{max_score, jdbcType=INTEGER}


SELECT 
	${fields}
FROM
	inscription
WHERE 
	account_id= #{accountId, jdbcType=BIGINT}
